input = ""

tests::Dict{String, Int64} = Dict{String, Int64}(
    "mjqjpqmgbljsphdztnvjfqwrcgsmlb" => 7,
    "bvwbjplbgvbhsrlpgdmjqwftvncz" => 5,
    "nppdvjthqldpwncqszvftbrmjlhg" => 6,
    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" => 10,
    "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" => 11
)

function is_start_of_packet(s4)
    (
        s4[1] != s4[2] &&
        s4[2] != s4[3] &&
        s4[3] != s4[4] &&
        s4[4] != s4[1] &&
        s4[1] != s4[3] &&
        s4[2] != s4[4]
    )
end

function find_start_of_packet(s)
    for index in 4:length(s)
        if is_start_of_packet(s[index .+ (-3:0)])
            return index
        end
    end
    return nothing
end

for (sequence, actual_start_of_sequence) in tests
    found_start_of_sequence = find_start_of_packet(sequence)
    try
        @assert found_start_of_sequence == actual_start_of_sequence
    catch AssertionError
        println(
            "Failed: $(sequence); ",
            "Found: $(found_start_of_sequence) $(sequence[found_start_of_sequence .+ (-3:0)]); ",
            "Actual: $(actual_start_of_sequence) $(sequence[actual_start_of_sequence .+ (-3:0)]); "
        )
    end
end

# a
find_start_of_packet(input)

# b
tests2::Dict{String, Int64} = Dict{String, Int64}(
    "mjqjpqmgbljsphdztnvjfqwrcgsmlb" => 19,
    "bvwbjplbgvbhsrlpgdmjqwftvncz" => 23,
    "nppdvjthqldpwncqszvftbrmjlhg" => 23,
    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" => 29,
    "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw" => 26
)

function is_start_of_message(s14)
    length(s14) == 14 &&
    length(Set(s14)) == 14
end

function find_start_of_message(s)
    for index in 14:length(s)
        if is_start_of_message(s[index .+ (-13:0)])
            return index
        end
    end
    return nothing
end

for (message, actual_start_of_message) in tests2
    found_start_of_message = find_start_of_message(message)
    try
        @assert found_start_of_message == actual_start_of_message
    catch
        found_message = (found_start_of_message < 14) ? "" : message[found_start_of_message .+ (-13:0)]
        actual_message = (actual_start_of_message < 14) ? "" : message[actual_start_of_message .+ (-13:0)]
        println(
            "Failed: $(message); ",
            "Found: $(found_start_of_message) $(found_message); ",
            "Actual: $(actual_start_of_message) $(actual_message); "
        )
    end
end

find_start_of_message(input)